

class Solution(object):
    def repeatedStringMatch(self, A, B):
        """
        :type A: str
        :type B: str
        :rtype: int
        """
        # A == B?
        # B contains character not in A?
        # Is Len of B < len of A
        # Is A substring of B?
        # How many times is A a substring of B? If A is repeated that many times, is B = or substring of A?

        A = A.lower()
        B = B.lower()

        if len(A) == 1:
            if len(set(B)) == 1:
                return B.count(A)
            else:
                return -1

        if A == B:
            return 1
        if len(B) < len(A):
            if B in A:
                return 1
            A *= 2
            if B in A:
                return 2
            return -1
        aCount = B.count(A)
        if aCount != 0:
            A *= aCount
            if A == B:
                return aCount
        else:
            return -1
        while len(A) <= len(B):
            aCount += 1
            A *= aCount
            if B in A:
                return aCount
        aCount += 1
        A *= aCount
        if B in A:
            return aCount

        return -1

s = Solution()
print(s.repeatedStringMatch("a", ""))



